import StoreServices from 'store-services/store-services'


export const getBooks = async () => {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      if (Math.random() > 0.75) {
        reject(new Error('Something bad happened'));
      } else {
        resolve(StoreServices.books);
      }
    }, 1000);
  });

}