import { books, phones } from './data-store'



const getBooksService = async () => {
  // return this.books
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      if (Math.random() > 0.75) {
        reject(new Error('Чтото случилось с загруской - Something bad happened'));
      } else {
        resolve(books);
      }
    }, 1000);
  });
}


