import * as R from 'ramda'

import { BOOKS_LOADED, LOAD_MORE_BOOKS } from 'actionTypes'


const initialState = {
  books: []
}

export default (state = initialState, { type, payload }) => { 
  // console.log('reduserPay = ', payload)
  switch (type) {
    case BOOKS_LOADED:
      const newState = R.indexBy(R.prop('id'), payload) //R.prop('id') = key
      return R.merge(state, newState)
      // return {
        // books: payload
        // ...state,
        // books: payload,
        // loading: false,
        // error: null
      // }
    
    case LOAD_MORE_BOOKS:
      const moreValues = R.indexBy(R.prop('id'), payload)
      return R.merge(state, moreValues)     
    
    
    default:  
      return state
  }; 

}
