import * as R from 'ramda'

import {
  BOOKS_LOADED,
  LOAD_MORE_BOOKS
} from 'actionTypes'

const initialState = {
  ids: []
}

// Получение ids у списка товаров по id
export default (state = initialState, {type, payload}) => {
  switch (type) {
    case BOOKS_LOADED:
      return R.merge(state, {
        ids: R.pluck('id', payload)
      })
    
     //здесь получаем ids и + добавляем к имеющимся ids конкатенацией
    case LOAD_MORE_BOOKS:
      const ids = R.pluck('id', payload)
      return R.merge(state, {
        ids: R.concat(ids, state.ids)
    })

    default:
      return state
  }
}