import * as R from 'ramda'

import {
  FETCH_PHONES_SUCCESS,
  LOAD_MORE_PHONES_SUCCESS,
  FETCH_PHONES_BY_ID_SUCCESS
} from 'actionTypes'


const initialState = {}

export default (state = initialState, { type, payload }) => {
  // console.log('REDUSER', type)
  switch (type) {
    // ------------------
    case FETCH_PHONES_SUCCESS:
      const newState = R.indexBy(R.prop('id'), payload) //R.prop('id') = key
      return R.merge(state, newState)
    // ------------------------
    case LOAD_MORE_PHONES_SUCCESS:
      //массив объектов преобразовываем в один объект
      const moreValues = R.indexBy(R.prop('id'), payload)
      return R.merge(state, moreValues)
    // --------------------------
    case FETCH_PHONES_BY_ID_SUCCESS:
      //assoc - записывает в поле объекта что даем id, значени, объект
      return R.assoc(payload.id, payload, state)
    
    default: 
      return state            
  }
}