import * as R from 'ramda'

import {
  FETCH_PHONES_SUCCESS,
  LOAD_MORE_PHONES_SUCCESS,
  SEARCH_PHONE
} from 'actionTypes'

const initialState = {
  ids: [], // ids - только тех телефонов, которые мы получили (за fetch)
  search: ''
}

export default (state = initialState, { type, payload }) => {
  switch (type) {
    case FETCH_PHONES_SUCCESS:
      //получаем  массив id из списка массива
      return R.merge(state, {
        ids: R.pluck('id', payload) //перетираем только id       
      })
    
      //здесь получаем ids и + добавляем к имеющимся ids конкатенацией
    case LOAD_MORE_PHONES_SUCCESS:      
      const ids = R.pluck('id', payload)// ids - которые добавились
      //state.ids - которые были ранее
      return R.merge(state, {
      ids: R.concat(ids, state.ids)
      })
    
    case SEARCH_PHONE:       
      return R.merge(state, { 
      search: payload
      })    
    
    default:
      return state
  }
}

