import { books, phones } from 'store-services/data-store-services'


export default class StoreServices {

  getBooksService = async () => {
  // return this.books
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        if (Math.random() > 0.75) {
          reject(new Error('Чтото случилось с загруской - Something bad happened'));
        } else {
          resolve(books);
        }
      }, 1000);
    });
  }

//для пагинации загруску данных надо проводить со сдвигом - offset
   getBooksMoreService = async ({ offset }) => {
    return new Promise(resolve => {
      resolve(books)
    })
  }

  // ----------------------------  
  getPhones = () => {
    return phones
  }

}


